import { useEffect, useState } from 'react';

import './Main.css';

function App(props: any) {
  const [state, setState] = useState('')


useEffect(() => {
  fetch(`https://api.openweathermap.org/data/2.5/weather?q=${props.formData.city},${props.formData.country}&appid=x`) // Replace YOUR_API_KEY with your actual API key
    .then(res => res.json())
    .then(data => {
      setState(data); 
    })
    .catch(error => {
      console.error("Error fetching data:", error);
    });
}, [props.buttonStatus]);


return (
  <div>

    <h1>Weather in {props.formData.city}, {props.formData.country}:</h1>
    <ul>
        {Object.entries(state).map(([key, value]) => (
          <li key={key}>
            <strong>{key}:</strong> {JSON.stringify(value)}
          </li>
        ))}
      </ul>
  </div>
);

}

export default App;
