import { useEffect, useState } from "react";
import './Location.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowsRotate, faSpinner } from '@fortawesome/free-solid-svg-icons'

function Location() {
    const lookup = require('country-code-lookup')

    let result: Country = lookup.byIso('CA');


    const [lat, setLat] = useState<number>();
    const [long, setLong] = useState<number>();

    interface Country {
        country: string;
    }

    interface WeatherData {
        name: string;
        cod: number;
        sys: {
            country: string;
        }
        wind: {
            speed: number;
        }
        main: {
            temp: number;
            feels_like: number;
            temp_min: number;
            temp_max: number;
        }
        weather: {
            description: string;
            icon: string;
        }[];
    }

    const [data, setData] = useState<WeatherData>({
        name: "",
        cod: 0,
        sys: {
            country: ""
        },
        wind: {
            speed: 0
        },
        main: {
            temp: 0,
            feels_like: 0,
            temp_min: 0,
            temp_max: 0
        },
        weather: [{
            description: "",
            icon: ""
        }]
    });

    const [errorMsg, setErrorMsg] = useState("");

    useEffect(() => {
        if ('geolocation' in navigator) {
            navigator.geolocation.getCurrentPosition(
                function (position) {
                setLat(position.coords.latitude)
                setLong(position.coords.longitude)
              },
              function (error) {
                if (error.code === error.PERMISSION_DENIED) {
                    setErrorMsg("User denied access to location");
                } else {
                    setErrorMsg("Error getting location:" + error.message);
                }
              }
            );
          } else {
            setErrorMsg("Geolocation API is not supported. Please try another browser");
          }
        }

    ,[]);

    useEffect(() => {
        if (lat && long) {
            fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=3289764f272a524f9cf22f194959aa13`)
            .then(res => res.json())
            .then(data => {
                setData(data); 
            })
            .catch(error => {
            console.error("Error fetching data:", error);
        });
    }

}, [lat, long]);



    function kelvin_to_celcius(k: number):number {
        return (Math.round(k-273.15))
    }

    function capitalize(sentence: string):string {
        for(let i=0; i<sentence.length; i++) {
            if (i===0 || sentence[i-1]===" ") {
                sentence = sentence.slice(0,i) + sentence[i].toUpperCase() + sentence.slice(i+1);             
            }
        }
        return sentence;
    }

    function refresh() {
        window.location.reload();
    }

    const date = new Date();
    return(
        (errorMsg !== "") ? (
            <>

                <div className="weather-body">
                <span>{errorMsg}</span>
                </div>

            </>
        ): (

            (data.name === "") ? (

                <div className="weather-body">
                    <span>Loading<FontAwesomeIcon icon={faSpinner} spin/></span>
                </div>


            ) : (

                <div className="weather-body">
                    <div className="weather-location-div">
                        <span className="weather-location">Weather in {data.name}, {data.sys.country}</span>
                        <span className="weather-time">As of {date.toLocaleTimeString()} <FontAwesomeIcon onClick={refresh} icon={faArrowsRotate} /></span>
                    </div>

                    <div className="weather-main">
                        <div className="weather-icon-div">
                            <img className="weather-icon" src={`https://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`} alt="weather-icon" />
                            <span className="weather-description">{capitalize(data.weather[0].description)}</span>
                        </div>
                        <div className="weather-main-temp-div">
                            <span className="weather-main-temp">{kelvin_to_celcius(data.main.temp)}°C</span>
                        </div>

                    </div>

                    <div className="info-main-div">
                        <div className="info-div">
                            <span className="info">Feels like</span>
                            <span className="info">{kelvin_to_celcius(data.main.feels_like)}°C</span>
                        </div>
                        <div className="info-div">
                            <span className="info">Feels like</span>
                            <span className="info">{kelvin_to_celcius(data.main.feels_like)}°C</span>
                        </div>
                        <div className="info-div">

                        </div>
                        <div className="info-div">

                        </div>


                            <span className="info">High {kelvin_to_celcius(data.main.temp_max)}°C</span>
                            <span className="info">Low {kelvin_to_celcius(data.main.temp_min)}°C</span>

                        <span className="info">Wind: {Math.round(data.wind.speed * 3.6)} km/h</span>   
                    </div>

                </div>
            )
        )                
    )
}

export default Location;
