import './Location.css'


function Location(props: any) {


    function kelvin_to_celcius(k: number):number {
        return (Math.round(k-273.15))
    }

    function capitalize(sentence: string):string {
        for(let i=0; i<sentence.length; i++) {
            if (i===0 || sentence[i-1]===" ") {
                sentence = sentence.slice(0,i) + sentence[i].toUpperCase() + sentence.slice(i+1);             
            }
        }
        return sentence;
    }



    return(
        (props.errorMsg !== "") ? (
            <>  
            <h1>hello</h1>
            </>
        ): (

            (props.data.name === "") ? (
            <>
            <h1>hello1</h1>
            </>                       
            ) : (
                <div>         
                    <div className="weather-main">
                        <div className="weather-icon-div">
                            <img className="weather-icon" src={`https://openweathermap.org/img/wn/${props.data.weather[0].icon}@2x.png`} alt="weather-icon" />
                            <span className="weather-description">{capitalize(props.data.weather[0].description)}</span>
                        </div>
                        <div className="weather-main-temp-div">
                            <span className="weather-main-temp">{kelvin_to_celcius(props.data.main.temp)}째C</span>
                        </div>

                    </div>

                    <div className="info-main-div">
                        <div className="info-div">
                            <span className="info">Feels like</span>
                            <span className="info">{kelvin_to_celcius(props.data.main.feels_like)}째C</span>
                        </div>
                        <div className="info-div">
                            <span className="info">High</span>
                            <span className="info">{kelvin_to_celcius(props.data.main.temp_max)}째C</span>
                        </div>
                        <div className="info-div">
                            <span className="info">Low</span>
                            <span className="info">{kelvin_to_celcius(props.data.main.temp_min)}째C</span>
                        </div>
                        <div className="info-div">
                            <span className="info">Wind</span>
                            <span className="info">{Math.round(props.data.wind.speed * 3.6)} km/h</span>
                        </div>
                    </div>

                </div>
            )
        )                
    )
}

export default Location;
