import { useEffect, useState } from 'react';
import './Options.css'

function Options(props: any) {
    const [optionsState, setOptionsState] = useState('')

    useEffect(() => {
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${props.formData.city},${props.formData.country}&appid=3289764f272a524f9cf22f194959aa13`) // Replace YOUR_API_KEY with your actual API key
          .then(res => res.json())
          .then(data => {
            setState(data); 
          })
          .catch(error => {
            console.error("Error fetching data:", error);
          });
      }, [props.formData.city, props.formData.country]);

    return (
        <></>
    )
}

export default Options;
