import { useEffect, useState } from "react";
import './Location.css'

function Location() {
    const lookup = require('country-code-lookup')

    let result: Country = lookup.byIso('CA');


    const [lat, setLat] = useState<number>();
    const [long, setLong] = useState<number>();

    interface Country {
        country: string;
    }

    interface WeatherData {
        name: string;
        cod: number;
        sys: {
            country: string;
            sunrise: number;
            sunset: number;
        }
        main: {
            temp: number;
            feels_like: number;
            temp_min: number;
            temp_max: number;
        }
        weather: {
            description: string;
            icon: string;
        }[];
    }

    const [data, setData] = useState<WeatherData>({
        name: "",
        cod: 0,
        sys: {
            country: "",
            sunrise: 0,
            sunset: 0
        },
        main: {
            temp: 0,
            feels_like: 0,
            temp_min: 0,
            temp_max: 0
        },
        weather: [{
            description: "",
            icon: ""
        }]
    });

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(function(position) {
            setLat(position.coords.latitude)
            setLong(position.coords.longitude)
        }
     )
    },[])

    useEffect(() => {
        if (lat && long) {

            fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=3289764f272a524f9cf22f194959aa13`)
            .then(res => res.json())
            .then(data => {
                setData(data); 
            })
            .catch(error => {
            console.error("Error fetching data:", error);
        });
    }

}, [lat, long]);

    const sunriseDate = new Date(data.sys.sunrise * 1000); 
    const sunsetDate = new Date(data.sys.sunset * 1000); 

    function kelvin_to_celcius(k: number):number {
        return (Math.round(k-273.15))
    }

    function capitalize(sentence: string):string {
        for(let i=0; i<sentence.length; i++) {
            if (i==0 || sentence[i-1]==" ") {
                sentence = sentence.slice(0,i) + sentence[i].toUpperCase() + sentence.slice(i+1);             
            }
        }
        return sentence;
    }

    return(
        <>
                <ul>
            {Object.entries(data).map(([key, value]) => (
            <li key={key}>
                <strong>{key}:</strong> {JSON.stringify(value)}
            </li>
            ))}
            </ul>
            <span>{lat}</span>
            <span>{long}</span>

            <div className="weather-body">
                <span className="weather-location">Weather in {data.name}, {data.sys.country}</span>
                <div className="weather-main">
                    <div className="weather-icon-div">
                         <img className="weather-icon" src={`https://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`} alt="weather-icon" />
                         <span className="weather-description">{capitalize(data.weather[0].description)}</span>
                    </div>
                    <div>
                        <span>{kelvin_to_celcius(data.main.temp)}°C</span>
                    </div>

                </div>

                <span>Feels like {kelvin_to_celcius(data.main.feels_like)}°C</span>


                <div>
                    <span>H {kelvin_to_celcius(data.main.temp_max)}°C/</span>
                    <span>L {kelvin_to_celcius(data.main.temp_min)}°C</span>
                </div>

                <span>Sunrise: {sunriseDate.getHours()}:{sunriseDate.getMinutes()}</span>
            </div>
        </>
    )
}

export default Location;
