import { useEffect, useState } from 'react';

import './Main.css';

function App(props: any) {
  const [state, setState] = useState('')
  const [city, setCity] = useState("Markham")
  const [country, setCountry] = useState("CA")


useEffect(() => {
  fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=3289764f272a524f9cf22f194959aa13`) // Replace YOUR_API_KEY with your actual API key
    .then(res => res.json())
    .then(data => {
      setState(data); // Assuming you want to display the weather description
    })
    .catch(error => {
      console.error("Error fetching data:", error);
    });
}, []);




function new_location() {

}

function city_change() {
  setCity(prev => city)
}

return (
  <div>

    <h1>Weather in {city}, {country}:</h1>
    <ul>
        {Object.entries(state).map(([key, value]) => (
          <li key={key}>
            <strong>{key}:</strong> {JSON.stringify(value)}
          </li>
        ))}
      </ul>
  </div>
);

}

export default App;
