import { useEffect, useState } from 'react';

import './App.css';

function App() {
  const [state, setState] = useState('')
  let city = "Markham"
  let country = "CA"

//   useEffect(() => {
//     return () => { fetch("http://api.openweathermap.org/geo/1.0/direct?q=Markham,CA&appid=3289764f272a524f9cf22f194959aa13")
//       .then(res => res.json())
//       .then(function getData(data) {
//         newState(data)
//       } )
// }}, [])

//   return (
//     <h1>{state}</h1>
//   )

useEffect(() => {
  fetch(`https://api.openweathermap.org/data/2.5/weather?q={{city}},{{country}}&appid=3289764f272a524f9cf22f194959aa13`) // Replace YOUR_API_KEY with your actual API key
    .then(res => res.json())
    .then(data => {
      setState(data); // Assuming you want to display the weather description
    })
    .catch(error => {
      console.error("Error fetching data:", error);
    });
}, []);

return (
  <div>
    <h1>Weather in {city}, {country}:</h1>
    <ul>
        {Object.entries(state).map(([key, value]) => (
          <li key={key}>
            <strong>{key}:</strong> {JSON.stringify(value)}
          </li>
        ))}
      </ul>
  </div>
);

}

export default App;
