import { useState } from 'react';
import './App.css';
import memesData from './memesData';

function App() {

  let [meme, setMemeState] = useState({
    top_text: "",
    bottom_text: "",
    image: "http://i.imgflip.com/1bij.jpg"
  })

  let [allMemesData, setAllMemesData] = useState(memesData)

  function getMemeImage(event: any) { 
    const {name, value} = event.target
    let rand_num = Math.floor(Math.random() * allMemesData.data.memes.length)
    setMemeState(prev => ({
      ...prev,
      [name] : value,
      image: allMemesData.data.memes[rand_num].url
    }))
  }

  function onChange(event) {
    const {name, value} = event.target

  }

  return (
    <div>
      <div className='input-container'>
        <input 
          name = "top_text"
          type="text"

        />
        <input 
          type="text"
          name = "bottom_text"

         />
      </div>
      <div className='output-container'>
        <button onClick={getMemeImage}>Get a new meme image</button>
        <div className='meme'>
          <img src={meme.image} alt="" />
          <h2 className="meme--text top">{meme.top_text}</h2>
          <h2 className="meme--text bottom">{meme.bottom_text}</h2>
        </div>

      </div>  
    </div>
  )
}

export default App;
