import { useState } from 'react';
import './App.css';
import memesData from './memesData';

function App() {
  let [memeState, setMemeState] = useState("")

  function new_link() {
    let memesArray = memesData.data.memes;
    let rand_num = Math.floor(Math.random() * memesArray.length)
    setMemeState(memesArray[rand_num].url)
  }

  return (
    <div>
      <div className='input-container'>
        <input type="text" />
        <input type="text" />
      </div>
      <div className='output-container'>
        <button onClick={new_link}>Get a new meme image</button>
        <img src={memeState} alt="" />
      </div>  
    </div>
  )
}

export default App;
