import { useEffect, useState } from "react";
import './Location.css'

function Location() {
    const lookup = require('country-code-lookup')

    let result: Country = lookup.byIso('CA');


    const [lat, setLat] = useState<number>();
    const [long, setLong] = useState<number>();

    interface Country {
        country: string;
    }

    interface WeatherData {
        name: string;
        cod: number;
        sys: {
            country: string;
            sunrise: number;
            sunset: number;
        }
        main: {
            temp: number;
            feels_like: number;
        }
        weather: {
            icon: string;
        }[];
    }

    const [data, setData] = useState<WeatherData>({
        name: "",
        cod: 0,
        sys: {
            country: "",
            sunrise: 0,
            sunset: 0
        },
        main: {
            temp: 0,
            feels_like: 0
        },
        weather: [{
            icon: ""
        }]
    });

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(function(position) {
            setLat(position.coords.latitude)
            setLong(position.coords.longitude)
        }
     )
    },[])

    useEffect(() => {
        if (lat && long) {

            fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=3289764f272a524f9cf22f194959aa13`)
            .then(res => res.json())
            .then(data => {
                setData(data); 
            })
            .catch(error => {
            console.error("Error fetching data:", error);
        });
    }

}, [lat, long]);

    const sunriseDate = new Date(data.sys.sunrise * 1000); 
    const sunsetDate = new Date(data.sys.sunset * 1000); 

    function kelvin_to_celcius(k: number) :number {
        return (Math.round(k-273.15))
    }

    return(
        <>
            <ul>
        {Object.entries(data).map(([key, value]) => (
          <li key={key}>
            <strong>{key}:</strong> {JSON.stringify(value)}
          </li>
        ))}
        </ul>
        <h1>{lat}</h1>
       <h1>{long}</h1>

       <h1>Cod: {data.cod}</h1>
       <h1>City: {data.name}</h1>
       <h1>Country: {data.sys.country}</h1>
       <h1>{result.country}</h1>
       <h1>Icon: {data.weather[0].icon}</h1>

            <div className="weather-body">
                <h1>Current weather in {data.name}</h1>
                <h1>Temperature: {kelvin_to_celcius(data.main.temp)}</h1>
                <h1>Feels like: {kelvin_to_celcius(data.main.feels_like)}</h1>

                <img className="weather-icon" src={`https://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`} alt="" />
                <h1>Sunrise: {sunriseDate.getHours()}:{sunriseDate.getMinutes()}</h1>
            </div>
        </>
    )
}

export default Location;
