  })

  let [allMemesData, setAllMemesData] = useState(meme)
  let memesArray = memesData.data.memes;

  function new_link() { 
    let rand_num = Math.floor(Math.random() * memesArray.length)
    setMemeState(memesArray[rand_num].url)
  }

